// Testowanie głównego endpointu books
cy.request("/books").then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.be.an("array");
  if (response.body.length > 0) {
    const book = response.body[0];
    // Sprawdzanie struktury danych książki
    expect(book).to.have.property("id");
    expect(book).to.have.property("name");
    expect(book).to.have.property("author");
    // Sprawdzanie czy ID jest liczbą
    expect(book.id).to.be.a("number");
    // Sprawdzanie czy nazwa jest stringiem
    expect(book.name).to.be.a("string");
  }
});
// Testowanie endpointu szczegółów książki
cy.request("/books/1/details").then(response => {
  expect(response.status).to.eq(200);
  const bookDetails = response.body;
  // Sprawdzanie struktury szczegółów książki
  expect(bookDetails).to.have.property("id");
  expect(bookDetails).to.have.property("name");
  expect(bookDetails).to.have.property("author");
  // Sprawdzanie struktury autora w szczegółach książki
  expect(bookDetails.author).to.have.property("id");
  expect(bookDetails.author).to.have.property("firstName");
  expect(bookDetails.author).to.have.property("lastName");
});

// Testowanie głównego endpointu authors
cy.request("/author").then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.be.an("array");
  if (response.body.length > 0) {
    const author = response.body[0];
    // Sprawdzanie struktury danych autora
    expect(author).to.have.property("author_id");
    expect(author).to.have.property("firstName");
    expect(author).to.have.property("lastName");
    expect(author).to.have.property("books");
    // Sprawdzanie czy ID jest liczbą
    expect(author.author_id).to.be.a("number");
    // Sprawdzanie czy imię i nazwisko są stringami
    expect(author.firstName).to.be.a("string");
    expect(author.lastName).to.be.a("string");
    // Sprawdzanie czy books jest tablicą
    expect(author.books).to.be.an("array");
  }
});
// Testowanie endpointu szczegółów autora
cy.request("/author/1/details").then(response => {
  expect(response.status).to.eq(200);
  const authorDetails = response.body;
  // Sprawdzanie struktury szczegółów autora
  expect(authorDetails).to.have.property("id");
  expect(authorDetails).to.have.property("firstName");
  expect(authorDetails).to.have.property("lastName");
  expect(authorDetails).to.have.property("books");
  // Sprawdzanie czy books jest tablicą
  expect(authorDetails.books).to.be.an("array");
  if (authorDetails.books.length > 0) {
    const book = authorDetails.books[0];
    // Sprawdzanie struktury książki w szczegółach autora
    expect(book).to.have.property("id");
    expect(book).to.have.property("name");
  }
});

const newBook = {
  name: "Nowa książka testowa",
  author_id: "1"
};
cy.request({
  method: "POST",
  url: "api/books/addBook",
  body: newBook
}).then(response => {
  expect(response.status).to.eq(200);
  // Sprawdzanie struktury odpowiedzi po dodaniu książki
  expect(response.body).to.have.property("id");
  expect(response.body).to.have.property("name");
  expect(response.body).to.have.property("author");
  expect(response.body.name).to.eq(newBook.name);
});

const newAuthor = {
