cy.get("button.check").click();
// Czekamy na wywołanie API gdy użytkownik kliknie przycisk "check"
cy.wait("@getBookDetails").then(interception => {
  expect(interception.request.url).to.match(/\/books\/\d+\/details/);
});
cy.get(".output-table").should("be.visible");

cy.visit("/dodajKsiazke.html");
// Submit without required fields
cy.get("button.Add").click();
cy.url().should("include", "/dodajKsiazke.html");
// Fill required fields and submit
cy.get("input.title").type("Test Book Title");
cy.get("select.author-id").select(1);
cy.get("button.Add").click();
// Sprawdzenie, czy request został wysłany do poprawnego endpointu
cy.wait("@addBook").then(interception => {
  expect(interception.request.url).to.include("/books/addBook");
  expect(interception.request.method).to.equal("POST");
  // Sprawdzenie, czy przesłane dane są zgodne z oczekiwaniami
  expect(interception.request.body).to.have.property("name", "Test Book Title");
  expect(interception.request.body).to.have.property("author_id");
});
cy.get(".added").should("not.have.class", "invis").and("contain", "Dodano ksiązkę");

cy.visit("/zaktualizujKsiazke.html");
// Select book and update fields
cy.get("select.book-id").select(1);
cy.get("input.title").type("Updated Book Title");
cy.get("input.author-id").type("1");
// Submit and check update message
cy.get('button[type="submit"]').click();
// Sprawdzenie czy request został wysłany do poprawnego endpointu
cy.wait("@updateBook").then(interception => {
  expect(interception.request.url).to.match(/\/books\/updateBook\/\d+/);
  expect(interception.request.method).to.equal("PUT");
  // Sprawdzenie, czy przesłane dane są zgodne z oczekiwaniami
  expect(interception.request.body).to.have.property("name", "Updated Book Title");
});
cy.get(".updated").should("not.have.class", "invis");

