cy.visit("/ksiazki.html");
cy.wait("@getBooks").then(interception => {
  // Sprawdzanie struktury odpowiedzi API
  expect(interception.response.body).to.be.an("array");
  if (interception.response.body.length > 0) {
    const book = interception.response.body[0];
    expect(book).to.have.property("id");
    expect(book).to.have.property("name");
    expect(book).to.have.property("author");
  }
});

